import { useEffect } from 'react'
import { BrowserRouter, Routes, Route, Navigate } from 'react-router-dom'
import { Toaster } from 'react-hot-toast'
import { useAuthStore } from './stores/authStore'
import ProtectedRoute from './components/ProtectedRoute'
import Login from './pages/Login'
import Dashboard from './pages/Dashboard'
import Portfolios from './pages/Portfolios'
import AssetsCatalog from './pages/AssetsCatalogHandsontable'
import Quotes from './pages/QuotesHandsontable'
import ImportData from './pages/ImportData'
import UsersCatalog from './pages/UsersCatalogHandsontable'

function App() {
  const { logout } = useAuthStore()

  useEffect(() => {
    // SIEMPRE hacer logout al cargar/recargar (F5)
    // Esto fuerza que el usuario tenga que hacer login cada vez
    logout().catch(() => {
      // Ignorar errores si no hay sesión
    })

    // Forzar navegación a login SIN cambiar la URL completa
    // Solo si no estamos ya en login
    if (window.location.pathname !== '/login') {
      window.history.replaceState(null, '', '/login')
    }
  }, [])

  return (
    <BrowserRouter>
      <Toaster position="top-right" />
      <Routes>
        {/* Ruta raíz siempre redirige a login */}
        <Route path="/" element={<Navigate to="/login" replace />} />
        <Route path="/login" element={<Login />} />
        <Route 
          path="/dashboard" 
          element={
            <ProtectedRoute>
              <Dashboard />
            </ProtectedRoute>
          } 
        />
        <Route 
          path="/portfolios" 
          element={
            <ProtectedRoute>
              <Portfolios />
            </ProtectedRoute>
          } 
        />
        <Route 
          path="/assets" 
          element={
            <ProtectedRoute>
              <AssetsCatalog />
            </ProtectedRoute>
          } 
        />
        <Route 
          path="/quotes" 
          element={
            <ProtectedRoute>
              <Quotes />
            </ProtectedRoute>
          } 
        />
        <Route 
          path="/import" 
          element={
            <ProtectedRoute>
              <ImportData />
            </ProtectedRoute>
          } 
        />
        <Route 
          path="/users" 
          element={
            <ProtectedRoute>
              <UsersCatalog />
            </ProtectedRoute>
          } 
        />
        {/* Redirigir cualquier ruta no encontrada a login */}
        <Route path="*" element={<Navigate to="/login" replace />} />
      </Routes>
    </BrowserRouter>
  )
}

export default App

import { useState, useEffect } from 'react';
import { X, Search } from 'lucide-react';
import { portfolioService } from '../services/portfolioService';
import type { Asset, CreateTransactionRequest } from '../types/portfolio';

interface AddTransactionModalProps {
  isOpen: boolean;
  onClose: () => void;
  portfolioId: string;  // UUID
  onSuccess: () => void;
}

export default function AddTransactionModal({ isOpen, onClose, portfolioId, onSuccess }: AddTransactionModalProps) {
  const [assets, setAssets] = useState<Asset[]>([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedAsset, setSelectedAsset] = useState<Asset | null>(null);
  const [transactionType, setTransactionType] = useState<'buy' | 'sell'>('buy');
  const [quantity, setQuantity] = useState('');
  const [price, setPrice] = useState('');
  const [fee, setFee] = useState('');
  const [notes, setNotes] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    if (isOpen) {
      loadAssets();
    }
  }, [isOpen]);

  const loadAssets = async () => {
    try {
      const data = await portfolioService.getAssets();
      setAssets(data);
    } catch (err) {
      console.error('Error loading assets:', err);
    }
  };

  const filteredAssets = assets.filter(asset =>
    asset.symbol.toLowerCase().includes(searchTerm.toLowerCase()) ||
    asset.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!selectedAsset) return;

    setError(null);
    setLoading(true);

    try {
      const data: CreateTransactionRequest = {
        asset_id: selectedAsset.id,
        transaction_type: transactionType,
        quantity: parseFloat(quantity),
        price: parseFloat(price),
        fee: fee ? parseFloat(fee) : undefined,
        notes: notes || undefined,
      };

      await portfolioService.createTransaction(portfolioId, data);

      // Reset form
      setSelectedAsset(null);
      setSearchTerm('');
      setQuantity('');
      setPrice('');
      setFee('');
      setNotes('');

      onSuccess();
      onClose();
    } catch (err: any) {
      setError(err.response?.data?.detail || 'Error al crear transacción');
    } finally {
      setLoading(false);
    }
  };

  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 z-50 overflow-y-auto">
      <div className="flex min-h-screen items-center justify-center p-4">
        <div className="fixed inset-0 bg-black bg-opacity-25" onClick={onClose} />

        <div className="relative bg-white rounded-lg shadow-xl max-w-2xl w-full p-6">
          <div className="flex items-center justify-between mb-4">
            <h3 className="text-lg font-medium text-gray-900">
              Agregar Transacción
            </h3>
            <button
              onClick={onClose}
              className="text-gray-400 hover:text-gray-500"
            >
              <X className="h-6 w-6" />
            </button>
          </div>

          {error && (
            <div className="mb-4 bg-red-50 border border-red-200 text-red-700 px-4 py-3 rounded">
              {error}
            </div>
          )}

          <form onSubmit={handleSubmit}>
            <div className="space-y-4">
              {/* Asset Selection */}
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Buscar Activo *
                </label>
                <div className="relative">
                  <Search className="absolute left-3 top-3 h-5 w-5 text-gray-400" />
                  <input
                    type="text"
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                    className="pl-10 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500 sm:text-sm"
                    placeholder="Buscar por símbolo o nombre..."
                  />
                </div>

                {searchTerm && (
                  <div className="mt-2 max-h-48 overflow-y-auto border border-gray-200 rounded-md">
                    {filteredAssets.length === 0 ? (
                      <div className="p-3 text-sm text-gray-500">No se encontraron activos</div>
                    ) : (
                      filteredAssets.map(asset => (
                        <button
                          key={asset.id}
                          type="button"
                          onClick={() => {
                            setSelectedAsset(asset);
                            setSearchTerm('');
                          }}
                          className="w-full text-left px-3 py-2 hover:bg-gray-50 flex items-center justify-between"
                        >
                          <div>
                            <div className="font-medium text-sm">{asset.symbol}</div>
                            <div className="text-xs text-gray-500">{asset.name}</div>
                          </div>
                          <span className="text-xs px-2 py-1 bg-blue-100 text-blue-800 rounded">
                            {asset.asset_type}
                          </span>
                        </button>
                      ))
                    )}
                  </div>
                )}

                {selectedAsset && (
                  <div className="mt-2 p-3 bg-indigo-50 border border-indigo-200 rounded-md flex items-center justify-between">
                    <div>
                      <div className="font-medium">{selectedAsset.symbol}</div>
                      <div className="text-sm text-gray-600">{selectedAsset.name}</div>
                    </div>
                    <button
                      type="button"
                      onClick={() => setSelectedAsset(null)}
                      className="text-indigo-600 hover:text-indigo-800 text-sm"
                    >
                      Cambiar
                    </button>
                  </div>
                )}
              </div>

              {/* Transaction Type */}
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Tipo de Transacción *
                </label>
                <div className="flex space-x-4">
                  <label className="flex items-center">
                    <input
                      type="radio"
                      value="buy"
                      checked={transactionType === 'buy'}
                      onChange={(e) => setTransactionType(e.target.value as 'buy')}
                      className="h-4 w-4 text-indigo-600 focus:ring-indigo-500"
                    />
                    <span className="ml-2 text-sm text-gray-700">Compra</span>
                  </label>
                  <label className="flex items-center">
                    <input
                      type="radio"
                      value="sell"
                      checked={transactionType === 'sell'}
                      onChange={(e) => setTransactionType(e.target.value as 'sell')}
                      className="h-4 w-4 text-indigo-600 focus:ring-indigo-500"
                    />
                    <span className="ml-2 text-sm text-gray-700">Venta</span>
                  </label>
                </div>
              </div>

              {/* Quantity and Price */}
              <div className="grid grid-cols-2 gap-4">
                <div>
                  <label htmlFor="quantity" className="block text-sm font-medium text-gray-700">
                    Cantidad *
                  </label>
                  <input
                    type="number"
                    id="quantity"
                    value={quantity}
                    onChange={(e) => setQuantity(e.target.value)}
                    required
                    step="0.00000001"
                    min="0"
                    className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500 sm:text-sm"
                    placeholder="0.00"
                  />
                </div>

                <div>
                  <label htmlFor="price" className="block text-sm font-medium text-gray-700">
                    Precio Unitario *
                  </label>
                  <input
                    type="number"
                    id="price"
                    value={price}
                    onChange={(e) => setPrice(e.target.value)}
                    required
                    step="0.01"
                    min="0"
                    className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500 sm:text-sm"
                    placeholder="0.00"
                  />
                </div>
              </div>

              {/* Fee */}
              <div>
                <label htmlFor="fee" className="block text-sm font-medium text-gray-700">
                  Comisión (opcional)
                </label>
                <input
                  type="number"
                  id="fee"
                  value={fee}
                  onChange={(e) => setFee(e.target.value)}
                  step="0.01"
                  min="0"
                  className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500 sm:text-sm"
                  placeholder="0.00"
                />
              </div>

              {/* Total */}
              {quantity && price && (
                <div className="bg-gray-50 p-3 rounded-md">
                  <div className="flex justify-between items-center">
                    <span className="text-sm font-medium text-gray-700">Total:</span>
                    <span className="text-lg font-bold text-gray-900">
                      ${(parseFloat(quantity) * parseFloat(price) + (fee ? parseFloat(fee) : 0)).toFixed(2)}
                    </span>
                  </div>
                </div>
              )}

              {/* Notes */}
              <div>
                <label htmlFor="notes" className="block text-sm font-medium text-gray-700">
                  Notas (opcional)
                </label>
                <textarea
                  id="notes"
                  value={notes}
                  onChange={(e) => setNotes(e.target.value)}
                  rows={2}
                  className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500 sm:text-sm"
                  placeholder="Información adicional sobre la transacción"
                />
              </div>
            </div>

            <div className="mt-6 flex justify-end space-x-3">
              <button
                type="button"
                onClick={onClose}
                className="px-4 py-2 text-sm font-medium text-gray-700 bg-white border border-gray-300 rounded-md hover:bg-gray-50"
              >
                Cancelar
              </button>
              <button
                type="submit"
                disabled={loading || !selectedAsset}
                className="px-4 py-2 text-sm font-medium text-white bg-indigo-600 border border-transparent rounded-md hover:bg-indigo-700 disabled:opacity-50"
              >
                {loading ? 'Guardando...' : 'Agregar Transacción'}
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
}

import { useState } from 'react';
import { X } from 'lucide-react';
import type { CreatePortfolioRequest } from '../types/portfolio';

interface CreatePortfolioModalProps {
  isOpen: boolean;
  onClose: () => void;
  onSubmit: (data: CreatePortfolioRequest) => Promise<void>;
}

export default function CreatePortfolioModal({ isOpen, onClose, onSubmit }: CreatePortfolioModalProps) {
  const [name, setName] = useState('');
  const [description, setDescription] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);

  if (!isOpen) return null;

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setError(null);
    setLoading(true);

    try {
      await onSubmit({ name, description: description || undefined });
      setName('');
      setDescription('');
      onClose();
    } catch (err: any) {
      setError(err.response?.data?.detail || 'Error al crear portfolio');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="fixed inset-0 z-50 overflow-y-auto">
      <div className="flex min-h-screen items-center justify-center p-4">
        <div className="fixed inset-0 bg-black bg-opacity-25" onClick={onClose} />

        <div className="relative bg-white rounded-lg shadow-xl max-w-md w-full p-6">
          <div className="flex items-center justify-between mb-4">
            <h3 className="text-lg font-medium text-gray-900">
              Crear Nuevo Portfolio
            </h3>
            <button
              onClick={onClose}
              className="text-gray-400 hover:text-gray-500"
            >
              <X className="h-6 w-6" />
            </button>
          </div>

          {error && (
            <div className="mb-4 bg-red-50 border border-red-200 text-red-700 px-4 py-3 rounded">
              {error}
            </div>
          )}

          <form onSubmit={handleSubmit}>
            <div className="space-y-4">
              <div>
                <label htmlFor="name" className="block text-sm font-medium text-gray-700">
                  Nombre *
                </label>
                <input
                  type="text"
                  id="name"
                  value={name}
                  onChange={(e) => setName(e.target.value)}
                  required
                  className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500 sm:text-sm"
                  placeholder="Ej: Portfolio Principal"
                />
              </div>

              <div>
                <label htmlFor="description" className="block text-sm font-medium text-gray-700">
                  Descripción
                </label>
                <textarea
                  id="description"
                  value={description}
                  onChange={(e) => setDescription(e.target.value)}
                  rows={3}
                  className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500 sm:text-sm"
                  placeholder="Descripción opcional del portfolio"
                />
              </div>
            </div>

            <div className="mt-6 flex justify-end space-x-3">
              <button
                type="button"
                onClick={onClose}
                className="px-4 py-2 text-sm font-medium text-gray-700 bg-white border border-gray-300 rounded-md hover:bg-gray-50"
              >
                Cancelar
              </button>
              <button
                type="submit"
                disabled={loading}
                className="px-4 py-2 text-sm font-medium text-white bg-indigo-600 border border-transparent rounded-md hover:bg-indigo-700 disabled:opacity-50"
              >
                {loading ? 'Creando...' : 'Crear Portfolio'}
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
}

import { useEffect, useState, useRef } from 'react';
import { portfolioService } from '../services/portfolioService';
import { snapshotService, type PortfolioSnapshot } from '../services/snapshotService';
import type { Portfolio, PositionWithPrice } from '../types/portfolio';
import { Wallet, TrendingUp, TrendingDown, Plus } from 'lucide-react';
import AddTransactionModal from './AddTransactionModal';
import CreatePortfolioModal from './CreatePortfolioModal';
import PortfolioDistributionChart from './PortfolioDistributionChart';
import PerformanceChart from './PerformanceChart';
import Handsontable from 'handsontable';
import 'handsontable/dist/handsontable.full.min.css';

export default function Dashboard() {
  const [portfolios, setPortfolios] = useState<Portfolio[]>([]);
  const [selectedPortfolio, setSelectedPortfolio] = useState<Portfolio | null>(null);
  const [positions, setPositions] = useState<PositionWithPrice[]>([]);
  const [availableDates, setAvailableDates] = useState<string[]>([]);
  const [selectedDate, setSelectedDate] = useState<string>('');
  const [selectedSnapshot, setSelectedSnapshot] = useState<PortfolioSnapshot | null>(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [showTransactionModal, setShowTransactionModal] = useState(false);
  const [showCreatePortfolioModal, setShowCreatePortfolioModal] = useState(false);
  const hotTableRef = useRef<HTMLDivElement>(null);
  const hotInstance = useRef<Handsontable | null>(null);

  useEffect(() => {
    loadPortfolios();
  }, []);

  useEffect(() => {
    if (selectedPortfolio) {
      loadPositions(selectedPortfolio.id);
      loadAvailableDates(selectedPortfolio.id);
    }
  }, [selectedPortfolio]);

  useEffect(() => {
    if (selectedDate && selectedPortfolio) {
      loadSnapshotByDate(selectedPortfolio.id, selectedDate);
    }
  }, [selectedDate]);

  useEffect(() => {
    if (selectedSnapshot && hotTableRef.current && !hotInstance.current) {
      initializeHandsontable();
    }
    if (selectedSnapshot && hotInstance.current) {
      updateHandsontableData();
    }
  }, [selectedSnapshot]);

  useEffect(() => {
    return () => {
      if (hotInstance.current) {
        hotInstance.current.destroy();
        hotInstance.current = null;
      }
    };
  }, []);

  const loadPortfolios = async () => {
    try {
      setLoading(true);
      const data = await portfolioService.getPortfolios();
      setPortfolios(data);
      if (data.length > 0 && !selectedPortfolio) {
        setSelectedPortfolio(data[0]);
      }
    } catch (err: any) {
      setError(err.message || 'Error al cargar portfolios');
    } finally {
      setLoading(false);
    }
  };

  const loadPositions = async (portfolioId: string) => {
    try {
      const data = await portfolioService.getPortfolioPrices(portfolioId);
      setPositions(data);
    } catch (err: any) {
      console.error('Error loading positions:', err);
    }
  };

  const loadAvailableDates = async (portfolioId: string) => {
    try {
      const dates = await snapshotService.getAvailableDates(portfolioId);
      setAvailableDates(dates);
      // Seleccionar la fecha más reciente por defecto
      if (dates.length > 0) {
        setSelectedDate(dates[0]);
      } else {
        // Si no hay fechas, intentar crear un snapshot para hoy
        try {
          await snapshotService.createSnapshot(portfolioId);
          // Recargar fechas
          const updatedDates = await snapshotService.getAvailableDates(portfolioId);
          setAvailableDates(updatedDates);
          if (updatedDates.length > 0) {
            setSelectedDate(updatedDates[0]);
          }
        } catch (createErr) {
          console.error('Error creating initial snapshot:', createErr);
        }
      }
    } catch (err: any) {
      console.error('Error loading available dates:', err);
    }
  };

  const loadSnapshotByDate = async (portfolioId: string, date: string) => {
    try {
      const snapshot = await snapshotService.getByDate(portfolioId, date);
      setSelectedSnapshot(snapshot);
    } catch (err: any) {
      console.error('Error loading snapshot by date:', err);
    }
  };

  const initializeHandsontable = () => {
    if (!hotTableRef.current || !selectedSnapshot) return;

    const data = selectedSnapshot.positions.map(p => ({
      symbol: p.symbol,
      name: p.name,
      asset_type: p.asset_type,
      quantity: p.quantity,
      average_price: p.average_price,
      current_price: p.current_price,
      current_value: p.current_value,
      cost_basis: p.cost_basis,
      profit_loss: p.profit_loss,
      profit_loss_percent: p.profit_loss_percent
    }));

    hotInstance.current = new Handsontable(hotTableRef.current, {
      data,
      colHeaders: [
        'Símbolo',
        'Nombre',
        'Tipo',
        'Cantidad',
        'Precio Promedio',
        'Precio Actual',
        'Valor Actual',
        'Costo Base',
        'Ganancia/Pérdida',
        'G/P %'
      ],
      columns: [
        { data: 'symbol', type: 'text', readOnly: true },
        { data: 'name', type: 'text', readOnly: true },
        { data: 'asset_type', type: 'text', readOnly: true },
        { data: 'quantity', type: 'numeric', readOnly: true, numericFormat: { pattern: '0,0.00' } },
        { data: 'average_price', type: 'numeric', readOnly: true, numericFormat: { pattern: '$0,0.00' } },
        { data: 'current_price', type: 'numeric', readOnly: true, numericFormat: { pattern: '$0,0.00' } },
        { data: 'current_value', type: 'numeric', readOnly: true, numericFormat: { pattern: '$0,0.00' } },
        { data: 'cost_basis', type: 'numeric', readOnly: true, numericFormat: { pattern: '$0,0.00' } },
        { 
          data: 'profit_loss', 
          type: 'numeric', 
          readOnly: true, 
          numericFormat: { pattern: '$0,0.00' },
          renderer: function(instance: any, td: HTMLTableCellElement, row: number, col: number, prop: any, value: any, cellProperties: any) {
            Handsontable.renderers.NumericRenderer.apply(this, [instance, td, row, col, prop, value, cellProperties]);
            if (value >= 0) {
              td.classList.add('htPositive');
            } else {
              td.classList.add('htNegative');
            }
            return td;
          }
        },
        { 
          data: 'profit_loss_percent', 
          type: 'numeric', 
          readOnly: true, 
          numericFormat: { pattern: '0.00%' },
          renderer: function(instance: any, td: HTMLTableCellElement, row: number, col: number, prop: any, value: any, cellProperties: any) {
            Handsontable.renderers.NumericRenderer.apply(this, [instance, td, row, col, prop, value, cellProperties]);
            if (value >= 0) {
              td.classList.add('htPositive');
            } else {
              td.classList.add('htNegative');
            }
            return td;
          }
        }
      ],
      rowHeaders: true,
      width: '100%',
      height: 400,
      licenseKey: 'non-commercial-and-evaluation',
      stretchH: 'all',
      autoColumnSize: true,
      filters: true,
      dropdownMenu: true,
      columnSorting: true
    });
  };

  const updateHandsontableData = () => {
    if (!hotInstance.current || !selectedSnapshot) return;

    const data = selectedSnapshot.positions.map(p => ({
      symbol: p.symbol,
      name: p.name,
      asset_type: p.asset_type,
      quantity: p.quantity,
      average_price: p.average_price,
      current_price: p.current_price,
      current_value: p.current_value,
      cost_basis: p.cost_basis,
      profit_loss: p.profit_loss,
      profit_loss_percent: p.profit_loss_percent
    }));

    hotInstance.current.loadData(data);
  };

  const calculateTotals = () => {
    if (selectedSnapshot) {
      return {
        totalValue: selectedSnapshot.total_value || 0,
        totalCost: selectedSnapshot.total_invested || selectedSnapshot.total_cost || 0,
        totalProfitLoss: selectedSnapshot.total_pnl || selectedSnapshot.total_profit_loss || 0,
        totalProfitLossPercent: selectedSnapshot.total_pnl_percent || selectedSnapshot.total_profit_loss_percent || 0
      };
    }

    const totalValue = positions.reduce((sum, p) => sum + (p.current_value || 0), 0);
    const totalCost = positions.reduce((sum, p) => sum + (p.cost_basis || 0), 0);
    const totalProfitLoss = totalValue - totalCost;
    const totalProfitLossPercent = totalCost > 0 ? (totalProfitLoss / totalCost) * 100 : 0;

    return {
      totalValue,
      totalCost,
      totalProfitLoss,
      totalProfitLossPercent
    };
  };

  const formatCurrency = (value: number) => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD',
      minimumFractionDigits: 2
    }).format(value);
  };

  const formatPercent = (value: number | undefined) => {
    if (value === undefined || value === null || isNaN(value)) return '0.00%';
    return value.toFixed(2) + '%';
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="text-gray-500">Cargando...</div>
      </div>
