import { useState } from 'react'
import api from '../services/api'
import Layout from '../components/Layout'

interface ImportStats {
  total: number
  created: number
  updated: number
  skipped: number
  errors: string[]
}

export default function ImportData() {
  const [activeTab, setActiveTab] = useState<'transactions' | 'quotes'>('transactions')
  const [portfolioId, setPortfolioId] = useState('')
  const [file, setFile] = useState<File | null>(null)
  const [skipDuplicates, setSkipDuplicates] = useState(true)
  const [isImporting, setIsImporting] = useState(false)
  const [importResult, setImportResult] = useState<ImportStats | null>(null)

  const handleFileChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    if (e.target.files && e.target.files[0]) {
      setFile(e.target.files[0])
      setImportResult(null)
    }
  }

  const handleImportTransactions = async () => {
    if (!file || !portfolioId) {
      alert('Por favor selecciona un archivo e introduce el ID de la cartera')
      return
    }

    setIsImporting(true)
    const formData = new FormData()
    formData.append('file', file)

    try {
      const response = await api.post(
        `/import-export/transactions/${portfolioId}/import?skip_duplicates=${skipDuplicates}`,
        formData,
        {
          headers: {
            'Content-Type': 'multipart/form-data',
          },
        }
      )
      setImportResult(response.data)
    } catch (error: any) {
      alert(error.response?.data?.detail || 'Error al importar')
    } finally {
      setIsImporting(false)
    }
  }

  const handleImportQuotes = async () => {
    if (!file) {
      alert('Por favor selecciona un archivo')
      return
    }

    setIsImporting(true)
    const formData = new FormData()
    formData.append('file', file)

    try {
      const response = await api.post(
        `/import-export/quotes/import?skip_duplicates=${skipDuplicates}`,
        formData,
        {
          headers: {
            'Content-Type': 'multipart/form-data',
          },
        }
      )
      setImportResult(response.data)
    } catch (error: any) {
      alert(error.response?.data?.detail || 'Error al importar')
    } finally {
      setIsImporting(false)
    }
  }

  const handleDownloadTemplate = async (type: 'transactions' | 'quotes') => {
    try {
      const response = await api.get(`/import-export/templates/${type}`, {
        responseType: 'blob'
      })

      const url = window.URL.createObjectURL(new Blob([response.data]))
      const link = document.createElement('a')
      link.href = url
      link.setAttribute('download', `${type}_template.csv`)
      document.body.appendChild(link)
      link.click()
      link.remove()
    } catch (error) {
      alert('Error al descargar la plantilla')
    }
  }

  return (
    <Layout>
      <div className="container mx-auto p-6 space-y-6">
      <div>
        <h1 className="text-3xl font-bold">Importar Datos</h1>
        <p className="text-gray-600">Importa transacciones y cotizaciones desde archivos CSV</p>
      </div>

      {/* Pestañas */}
      <div className="border-b border-gray-200">
        <nav className="-mb-px flex space-x-8">
          <button
            onClick={() => setActiveTab('transactions')}
            className={`py-4 px-1 border-b-2 font-medium text-sm ${
              activeTab === 'transactions'
                ? 'border-blue-500 text-blue-600'
                : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
            }`}
          >
            Transacciones
          </button>
          <button
            onClick={() => setActiveTab('quotes')}
            className={`py-4 px-1 border-b-2 font-medium text-sm ${
              activeTab === 'quotes'
                ? 'border-blue-500 text-blue-600'
                : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
            }`}
          >
            Cotizaciones
          </button>
        </nav>
      </div>

      {/* Contenido */}
      <div className="bg-white rounded-lg shadow p-6 space-y-6">
        {/* Descargar Plantilla */}
        <div className="bg-blue-50 border border-blue-200 rounded-lg p-4">
          <h3 className="font-semibold mb-2">📄 Descargar Plantilla</h3>
          <p className="text-sm text-gray-600 mb-3">
            Descarga una plantilla CSV para ver el formato requerido
          </p>
          <button
            onClick={() => handleDownloadTemplate(activeTab)}
            className="px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
          >
            Descargar Plantilla de {activeTab === 'transactions' ? 'Transacciones' : 'Cotizaciones'}
          </button>
        </div>

        {/* Formulario de Importación */}
        <div className="space-y-4">
          <h3 className="font-semibold text-lg">Importar {activeTab === 'transactions' ? 'Transacciones' : 'Cotizaciones'}</h3>

          {activeTab === 'transactions' && (
            <div>
              <label className="block text-sm font-medium mb-1">ID de Cartera *</label>
              <input
                type="text"
                value={portfolioId}
                onChange={(e) => setPortfolioId(e.target.value)}
                placeholder="Introduce el UUID de la cartera"
                className="w-full px-3 py-2 border rounded"
              />
            </div>
          )}

          <div>
            <label className="block text-sm font-medium mb-1">Archivo CSV *</label>
            <input
              type="file"
              accept=".csv"
              onChange={handleFileChange}
              className="w-full px-3 py-2 border rounded"
            />
            {file && (
              <p className="text-sm text-gray-600 mt-1">
                Seleccionado: {file.name} ({(file.size / 1024).toFixed(2)} KB)
              </p>
            )}
          </div>

          <div className="flex items-center">
            <input
              type="checkbox"
              id="skipDuplicates"
              checked={skipDuplicates}
              onChange={(e) => setSkipDuplicates(e.target.checked)}
              className="h-4 w-4 text-blue-600 rounded"
            />
            <label htmlFor="skipDuplicates" className="ml-2 text-sm">
              Omitir duplicados (recomendado)
            </label>
          </div>

          <button
            onClick={activeTab === 'transactions' ? handleImportTransactions : handleImportQuotes}
            disabled={isImporting || !file || (activeTab === 'transactions' && !portfolioId)}
            className="w-full px-4 py-2 bg-green-600 text-white rounded hover:bg-green-700 disabled:bg-gray-400"
          >
            {isImporting ? 'Importando...' : `Importar ${activeTab === 'transactions' ? 'Transacciones' : 'Cotizaciones'}`}
          </button>
        </div>

        {/* Resultados de la Importación */}
        {importResult && (
          <div className="border-t pt-6">
            <h3 className="font-semibold text-lg mb-4">Resultados de la Importación</h3>
            <div className="grid grid-cols-2 md:grid-cols-4 gap-4 mb-4">
              <div className="bg-gray-50 p-4 rounded">
                <div className="text-2xl font-bold text-gray-900">{importResult.total}</div>
                <div className="text-sm text-gray-600">Total</div>
              </div>
              <div className="bg-green-50 p-4 rounded">
                <div className="text-2xl font-bold text-green-600">{importResult.created}</div>
                <div className="text-sm text-gray-600">Creados</div>
              </div>
              {importResult.updated > 0 && (
                <div className="bg-blue-50 p-4 rounded">
                  <div className="text-2xl font-bold text-blue-600">{importResult.updated}</div>
                  <div className="text-sm text-gray-600">Actualizados</div>
                </div>
              )}
              <div className="bg-yellow-50 p-4 rounded">
                <div className="text-2xl font-bold text-yellow-600">{importResult.skipped}</div>
                <div className="text-sm text-gray-600">Omitidos</div>
              </div>
            </div>

            {importResult.errors.length > 0 && (
              <div className="bg-red-50 border border-red-200 rounded-lg p-4">
                <h4 className="font-semibold text-red-900 mb-2">Errores ({importResult.errors.length})</h4>
                <ul className="list-disc list-inside space-y-1 text-sm text-red-800">
                  {importResult.errors.map((error, idx) => (
                    <li key={idx}>{error}</li>
                  ))}
                </ul>
              </div>
            )}
          </div>
        )}

        {/* Información del Formato */}
        <div className="border-t pt-6">
          <h3 className="font-semibold text-lg mb-3">Información del Formato</h3>

          {activeTab === 'transactions' ? (
            <div className="bg-gray-50 p-4 rounded text-sm space-y-2">
              <p><strong>Columnas requeridas:</strong> date, type, asset_symbol, quantity, price</p>
              <p><strong>Columnas opcionales:</strong> fees, notes</p>
              <p><strong>Valores de tipo:</strong> BUY, SELL</p>
              <p><strong>Formato de fecha:</strong> YYYY-MM-DD</p>
              <p className="text-gray-600 italic">
                Ejemplo: 2025-01-15,BUY,AAPL,10,150.50,9.99,Compra inicial
              </p>
            </div>
          ) : (
            <div className="bg-gray-50 p-4 rounded text-sm space-y-2">
              <p><strong>Columnas requeridas:</strong> symbol, date, open, high, low, close</p>
              <p><strong>Columnas opcionales:</strong> volume, source</p>
              <p><strong>Formato de fecha:</strong> YYYY-MM-DD</p>
              <p className="text-gray-600 italic">
                Ejemplo: AAPL,2025-01-15,150.00,152.00,149.50,151.50,1000000,manual
              </p>
            </div>
          )}
        </div>
      </div>
      </div>
    </Layout>
  )
}

import { useState, useEffect } from 'react'
import { useNavigate } from 'react-router-dom'
import { useAuthStore } from '../stores/authStore'
import { toast } from 'react-hot-toast'

export default function Login() {
  const navigate = useNavigate()
  const { user, login } = useAuthStore()
  const [username, setUsername] = useState('')
  const [password, setPassword] = useState('')
  const [isSubmitting, setIsSubmitting] = useState(false)

  console.log('Página de inicio de sesión renderizando')

  // Redirigir a dashboard después del login exitoso
  useEffect(() => {
    if (user) {
      console.log('Usuario autenticado, redirigiendo al dashboard')
      navigate('/dashboard', { replace: true })
    }
  }, [user, navigate])

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault()

    if (isSubmitting) return

    setIsSubmitting(true)
    try {
      const trimmedUsername = username.trim()
      const trimmedPassword = password.trim()
      console.log('Intentando iniciar sesión con el usuario:', trimmedUsername)
      await login(trimmedUsername, trimmedPassword)
      console.log('Inicio de sesión exitoso, navegando al panel de control')
      toast.success('¡Inicio de sesión exitoso!')
      // La redirección se hará automáticamente por el useEffect
    } catch (error: any) {
      console.error('Error de inicio de sesión:', error)
      const message = error.response?.data?.detail || error.message || 'Error al iniciar sesión'
      toast.error(message)
    } finally {
      setIsSubmitting(false)
    }
  }

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-blue-900 to-blue-600">
      <div className="bg-white p-8 rounded-lg shadow-xl w-96">
        <h1 className="text-3xl font-bold text-center mb-6 text-gray-900">BolsaV2</h1>
        <form onSubmit={handleSubmit} className="space-y-4">
          <div>
            <label className="block text-sm font-medium text-gray-700">Usuario</label>
            <input
              type="text"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              className="mt-1 block w-full rounded-md border border-gray-300 px-3 py-2"
              required
            />
          </div>
          <div>
            <label className="block text-sm font-medium text-gray-700">Contraseña</label>
            <input
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              className="mt-1 block w-full rounded-md border border-gray-300 px-3 py-2"
              required
            />
          </div>
          <button
            type="submit"
            disabled={isSubmitting}
            className="w-full bg-blue-600 text-white py-2 rounded-md hover:bg-blue-700 disabled:opacity-50 disabled:cursor-not-allowed"
          >
            {isSubmitting ? 'Iniciando sesión...' : 'Iniciar Sesión'}
          </button>
        </form>
      </div>
    </div>
  )
}

import { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuthStore } from '../stores/authStore';
import { portfolioService } from '../services/portfolioService';
import CreatePortfolioModal from '../components/CreatePortfolioModal';
import Layout from '../components/Layout';
import type { Portfolio } from '../types/portfolio';
import { Plus, Wallet, Trash2 } from 'lucide-react';

export default function Portfolios() {
  const { user, isLoading } = useAuthStore();
  const navigate = useNavigate();
  const [portfolios, setPortfolios] = useState<Portfolio[]>([]);
  const [loading, setLoading] = useState(true);
  const [showCreateModal, setShowCreateModal] = useState(false);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    if (!isLoading && !user) {
      navigate('/login');
      return;
    }
    if (user) {
      loadPortfolios();
    }
  }, [user, isLoading, navigate]);

  const loadPortfolios = async () => {
    try {
      setLoading(true);
      const data = await portfolioService.getPortfolios();
      setPortfolios(data);
    } catch (err: any) {
      setError(err.message || 'Error al cargar portfolios');
    } finally {
      setLoading(false);
    }
  };

  const handleCreatePortfolio = async (data: { name: string; description?: string }) => {
    await portfolioService.createPortfolio(data);
    await loadPortfolios();
  };

  const handleDeletePortfolio = async (id: string) => {
    if (!confirm('¿Estás seguro de eliminar este portfolio?')) return;

    try {
      await portfolioService.deletePortfolio(id);
      await loadPortfolios();
    } catch (err: any) {
      alert(err.response?.data?.detail || 'Error al eliminar portfolio');
    }
  };

  if (isLoading || !user) return null;

  return (
    <Layout>
      <div className="mb-6 flex justify-between items-center">
          <div>
            <h2 className="text-3xl font-bold text-gray-900">Mis Portfolios</h2>
            <p className="mt-2 text-sm text-gray-600">
              Gestiona tus portfolios de inversión
            </p>
          </div>
          <button
            onClick={() => setShowCreateModal(true)}
            className="inline-flex items-center px-4 py-2 border border-transparent shadow-sm text-sm font-medium rounded-md text-white bg-indigo-600 hover:bg-indigo-700"
          >
            <Plus className="h-5 w-5 mr-2" />
            Nuevo Portfolio
          </button>
        </div>

        {error && (
          <div className="mb-4 bg-red-50 border border-red-200 text-red-700 px-4 py-3 rounded">
            {error}
          </div>
        )}

        {loading ? (
          <div className="flex items-center justify-center h-64">
            <div className="text-gray-500">Cargando...</div>
          </div>
        ) : portfolios.length === 0 ? (
          <div className="text-center py-12 bg-white rounded-lg shadow">
            <Wallet className="mx-auto h-12 w-12 text-gray-400" />
            <h3 className="mt-2 text-sm font-medium text-gray-900">No hay portfolios</h3>
            <p className="mt-1 text-sm text-gray-500">
              Crea tu primer portfolio para comenzar a gestionar tus inversiones.
            </p>
            <div className="mt-6">
              <button
                onClick={() => setShowCreateModal(true)}
                className="inline-flex items-center px-4 py-2 border border-transparent shadow-sm text-sm font-medium rounded-md text-white bg-indigo-600 hover:bg-indigo-700"
              >
                <Plus className="h-5 w-5 mr-2" />
                Crear Primer Portfolio
              </button>
            </div>
          </div>
        ) : (
          <div className="grid grid-cols-1 gap-6 sm:grid-cols-2 lg:grid-cols-3">
            {portfolios.map((portfolio) => (
              <div
                key={portfolio.id}
                className="bg-white overflow-hidden shadow rounded-lg hover:shadow-lg transition-shadow"
              >
                <div className="p-6">
                  <div className="flex items-center justify-between mb-4">
                    <div className="flex items-center">
                      <Wallet className="h-8 w-8 text-indigo-600" />
                      <h3 className="ml-3 text-lg font-medium text-gray-900">
                        {portfolio.name}
                      </h3>
                    </div>
                  </div>

                  {portfolio.description && (
                    <p className="text-sm text-gray-600 mb-4">
                      {portfolio.description}
                    </p>
                  )}

                  <div className="text-xs text-gray-500 mb-4">
                    Creado: {new Date(portfolio.created_at).toLocaleDateString('es-ES')}
                  </div>

                  <div className="flex space-x-2">
                    <button
                      onClick={() => navigate(`/?portfolio=${portfolio.id}`)}
                      className="flex-1 px-3 py-2 text-sm font-medium text-indigo-600 bg-indigo-50 rounded hover:bg-indigo-100"
                    >
                      Ver Detalles
                    </button>
                    <button
                      onClick={() => handleDeletePortfolio(portfolio.id)}
                      className="px-3 py-2 text-sm font-medium text-red-600 bg-red-50 rounded hover:bg-red-100"
                    >
                      <Trash2 className="h-4 w-4" />
                    </button>
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}

      <CreatePortfolioModal
        isOpen={showCreateModal}
        onClose={() => setShowCreateModal(false)}
        onSubmit={handleCreatePortfolio}
      />
    </Layout>
  );
}

import { useState, useEffect } from 'react'
import api from '../services/api'
import Layout from '../components/Layout'

interface Asset {
  id: string
  symbol: string
  name: string
}

interface Quote {
  symbol: string
  date: string
  close: number
  open?: number
  high?: number
  low?: number
  volume?: number
}

export default function Quotes() {
  const [assets, setAssets] = useState<Asset[]>([])
  const [quotes, setQuotes] = useState<Quote[]>([])
  const [isLoading, setIsLoading] = useState(false)
  const [selectedAsset, setSelectedAsset] = useState<string>('all')
  const [startDate, setStartDate] = useState('')
  const [endDate, setEndDate] = useState('')
  const [datesDisabled, setDatesDisabled] = useState(true)

  useEffect(() => {
    loadAssets()
  }, [])

  useEffect(() => {
    if (selectedAsset === 'all') {
      setDatesDisabled(true)
      setStartDate('')
      setEndDate('')
    } else {
      setDatesDisabled(false)
    }
  }, [selectedAsset])

  const loadAssets = async () => {
    try {
      const response = await api.get('/assets')
      setAssets(response.data)
    } catch (error) {
      console.error('Error al cargar activos:', error)
      alert('Error al cargar activos')
    }
  }

  const loadLatestQuotes = async () => {
    setIsLoading(true)
    try {
      // Obtener la última cotización de cada activo
      const response = await api.get('/quotes', {
        params: { limit: 1000 }
      })

      // Agrupar por símbolo y mantener solo la cotización más reciente de cada uno
      const quotesBySymbol = new Map<string, Quote>()
      response.data.forEach((quote: Quote) => {
        const existing = quotesBySymbol.get(quote.symbol)
        if (!existing || new Date(quote.date) > new Date(existing.date)) {
          quotesBySymbol.set(quote.symbol, quote)
        }
      })

      // Convertir a array y ordenar por símbolo
      const latestQuotes = Array.from(quotesBySymbol.values()).sort((a, b) => 
        a.symbol.localeCompare(b.symbol)
      )

      setQuotes(latestQuotes)
    } catch (error) {
      console.error('Error al cargar cotizaciones:', error)
      alert('Error al cargar cotizaciones')
    } finally {
      setIsLoading(false)
    }
  }

  const loadFilteredQuotes = async () => {
    if (selectedAsset === 'all') {
      loadLatestQuotes()
      return
    }

    if (!startDate || !endDate) {
      alert('Por favor, selecciona las fechas de inicio y fin')
      return
    }

    if (new Date(startDate) > new Date(endDate)) {
      alert('La fecha de inicio debe ser anterior a la fecha de fin')
      return
    }

    setIsLoading(true)
    try {
      const response = await api.get('/quotes', {
        params: {
          symbol: selectedAsset,
          start_date: startDate,
          end_date: endDate,
          limit: 1000
        }
      })

      // Ordenar por fecha descendente (más reciente primero)
      const sortedQuotes = response.data.sort((a: Quote, b: Quote) => 
        new Date(b.date).getTime() - new Date(a.date).getTime()
      )

      setQuotes(sortedQuotes)
    } catch (error) {
      console.error('Error al cargar cotizaciones:', error)
      alert('Error al cargar cotizaciones filtradas')
    } finally {
      setIsLoading(false)
    }
  }

  const handleSearch = () => {
    loadFilteredQuotes()
  }

  const formatDate = (dateString: string) => {
    const date = new Date(dateString)
    return date.toLocaleDateString('es-ES', {
      year: 'numeric',
      month: '2-digit',
      day: '2-digit'
    })
  }

  const formatCurrency = (value: number) => {
    return new Intl.NumberFormat('es-ES', {
      style: 'currency',
      currency: 'USD',
      minimumFractionDigits: 2,
      maximumFractionDigits: 4
    }).format(value)
  }

  const getAssetName = (symbol: string) => {
    const asset = assets.find(a => a.symbol === symbol)
    return asset ? `${asset.name} (${symbol})` : symbol
  }

  return (
    <Layout>
      <div className="container-fluid">
        <div className="d-flex justify-content-between align-items-center mb-4">
          <h2>📊 Cotizaciones</h2>
        </div>

        {/* Filtros */}
        <div className="card mb-4">
          <div className="card-body">
            <div className="row g-3">
              {/* Selector de Activos */}
              <div className="col-md-4">
                <label htmlFor="assetSelect" className="form-label">Activo</label>
                <select
                  id="assetSelect"
                  className="form-select"
                  value={selectedAsset}
                  onChange={(e) => setSelectedAsset(e.target.value)}
                >
                  <option value="all">Todos los activos</option>
                  {assets.map(asset => (
                    <option key={asset.id} value={asset.symbol}>
                      {asset.name} ({asset.symbol})
                    </option>
                  ))}
                </select>
              </div>

              {/* Fecha Inicio */}
              <div className="col-md-3">
                <label htmlFor="startDate" className="form-label">Fecha Inicio</label>
                <input
                  type="date"
                  id="startDate"
                  className="form-control"
                  value={startDate}
                  onChange={(e) => setStartDate(e.target.value)}
                  disabled={datesDisabled}
                />
              </div>

              {/* Fecha Fin */}
              <div className="col-md-3">
                <label htmlFor="endDate" className="form-label">Fecha Fin</label>
                <input
                  type="date"
                  id="endDate"
                  className="form-control"
                  value={endDate}
                  onChange={(e) => setEndDate(e.target.value)}
                  disabled={datesDisabled}
                />
              </div>

              {/* Botón Buscar */}
              <div className="col-md-2 d-flex align-items-end">
                <button
                  className="btn btn-primary w-100"
                  onClick={handleSearch}
                  disabled={isLoading}
                >
                  {isLoading ? '🔄 Buscando...' : '🔍 Buscar'}
                </button>
              </div>
            </div>

            {selectedAsset === 'all' && (
              <div className="alert alert-info mt-3 mb-0">
                <small>
                  ℹ️ Mostrando las últimas cotizaciones registradas para todos los activos
                </small>
              </div>
            )}
          </div>
        </div>

        {/* Tabla de Cotizaciones */}
        <div className="card">
          <div className="card-body">
            <h5 className="card-title">
              Resultados {quotes.length > 0 && `(${quotes.length})`}
            </h5>

            {isLoading ? (
              <div className="text-center py-5">
                <div className="spinner-border text-primary" role="status">
                  <span className="visually-hidden">Cargando...</span>
                </div>
              </div>
            ) : quotes.length === 0 ? (
              <div className="text-center text-muted py-5">
                <p>No se encontraron cotizaciones</p>
                <small>
                  {selectedAsset === 'all' 
                    ? 'Presiona "Buscar" para cargar las últimas cotizaciones de todos los activos'
                    : 'Selecciona un rango de fechas y presiona "Buscar"'
                  }
                </small>
              </div>
            ) : (
              <div className="table-responsive">
                <table className="table table-hover table-striped">
                  <thead>
                    <tr>
                      <th>Fecha</th>
                      <th>Activo</th>
                      <th className="text-end">Cotización</th>
                      <th className="text-end">Apertura</th>
                      <th className="text-end">Máximo</th>
                      <th className="text-end">Mínimo</th>
                      <th className="text-end">Volumen</th>
                    </tr>
                  </thead>
                  <tbody>
                    {quotes.map((quote, index) => (
                      <tr key={`${quote.symbol}-${quote.date}-${index}`}>
                        <td>{formatDate(quote.date)}</td>
                        <td>
                          <strong>{getAssetName(quote.symbol)}</strong>
                        </td>
                        <td className="text-end">
                          <strong>{formatCurrency(quote.close)}</strong>
                        </td>
                        <td className="text-end">
                          {quote.open ? formatCurrency(quote.open) : '-'}
                        </td>
                        <td className="text-end">
                          {quote.high ? formatCurrency(quote.high) : '-'}
                        </td>
                        <td className="text-end">
                          {quote.low ? formatCurrency(quote.low) : '-'}
                        </td>
                        <td className="text-end">
                          {quote.volume
                            ? new Intl.NumberFormat('es-ES').format(quote.volume)
                            : '-'}
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            )}
          </div>
        </div>
      </div>
    </Layout>
  )
}

