          </h5>

          {isLoading ? (
            <div className="text-center py-12">
              <div className="inline-block animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600"></div>
              <p className="mt-4 text-gray-600">Cargando...</p>
            </div>
          ) : quotes.length === 0 ? (
            <div className="text-center text-gray-500 py-12">
              <p>No se encontraron cotizaciones</p>
              <small className="text-gray-400">
                {selectedAsset === 'all' 
                  ? 'Presiona "Buscar" para cargar las últimas cotizaciones de todos los activos'
                  : 'Selecciona un rango de fechas y presiona "Buscar"'
                }
              </small>
            </div>
          ) : (
            <div className="overflow-x-auto">
              <HotTable
                ref={hotTableRef}
                data={tableData}
                columns={columns}
                colHeaders={true}
                rowHeaders={true}
                height="500"
                licenseKey="non-commercial-and-evaluation"
                stretchH="all"
                autoWrapRow={true}
                autoWrapCol={true}
                filters={true}
                dropdownMenu={true}
                columnSorting={true}
                manualColumnResize={true}
                contextMenu={true}
                language="es-MX"
              />
            </div>
          )}
        </div>
      </div>
    </Layout>
  )
}

import { useState, useEffect } from 'react'
import api from '../services/api'
import Layout from '../components/Layout'

interface User {
  id: string
  username: string
  email: string
  is_active: boolean
  created_at: string
}

export default function UsersCatalog() {
  const [users, setUsers] = useState<User[]>([])
  const [isLoading, setIsLoading] = useState(true)
  const [showModal, setShowModal] = useState(false)
  const [formData, setFormData] = useState({
    username: '',
    email: '',
    password: ''
  })

  useEffect(() => {
    loadUsers()
  }, [])

  const loadUsers = async () => {
    setIsLoading(true)
    try {
      const response = await api.get('/users')
      setUsers(response.data)
    } catch (error) {
      console.error('Failed to load users:', error)
      alert('This feature requires admin privileges')
    } finally {
      setIsLoading(false)
    }
  }

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault()

    if (!formData.username || !formData.email || !formData.password) {
      alert('Todos los campos son obligatorios')
      return
    }

    try {
      await api.post('/auth/register', formData)
      alert('Usuario creado correctamente')
      setShowModal(false)
      setFormData({ username: '', email: '', password: '' })
      loadUsers()
    } catch (error: any) {
      alert(error.response?.data?.detail || 'Error al crear usuario')
    }
  }

  const toggleUserStatus = async (user: User) => {
    try {
      await api.patch(`/users/${user.id}`, {
        is_active: !user.is_active
      })
      alert(`Usuario ${user.is_active ? 'desactivado' : 'activado'} correctamente`)
      loadUsers()
    } catch (error: any) {
      alert(error.response?.data?.detail || 'Operación fallida')
    }
  }

  const handleDelete = async (user: User) => {
    if (!confirm(`¿Eliminar usuario ${user.username}? Esta acción no se puede deshacer.`)) return

    try {
      await api.delete(`/users/${user.id}`)
      alert('Usuario eliminado correctamente')
      loadUsers()
    } catch (error: any) {
      alert(error.response?.data?.detail || 'Error al eliminar')
    }
  }

  if (isLoading) {
    return (
      <Layout>
        <div className="flex h-96 items-center justify-center">
          <div className="text-xl">Cargando usuarios...</div>
        </div>
      </Layout>
    )
  }

  return (
    <Layout>
      <div className="container mx-auto p-6 space-y-6">
      {/* Cabecera */}
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-3xl font-bold">Gestión de Usuarios</h1>
          <p className="text-gray-600">Gestiona los usuarios del sistema (Solo administradores)</p>
        </div>
        <button
          onClick={() => setShowModal(true)}
          className="px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
        >
          + Crear Usuario
        </button>
      </div>

      {/* Tabla de Usuarios */}
      <div className="bg-white rounded-lg shadow overflow-hidden">
        <table className="w-full">
          <thead className="bg-gray-50">
            <tr>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Usuario</th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Correo</th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Estado</th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Creado</th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Acciones</th>
            </tr>
          </thead>
          <tbody className="bg-white divide-y divide-gray-200">
            {users.map((user) => (
              <tr key={user.id}>
                <td className="px-6 py-4 whitespace-nowrap font-medium">{user.username}</td>
                <td className="px-6 py-4">{user.email}</td>
